import React, { useEffect, useRef } from "react";
import s from "./skillsSection.module.css";
import Heading from "../../shared/Heading";
import { SectionWrapper } from "../../shared/SectionWrapper";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

const SkillsSection = () => {
  const [ref, inView] = useInView();
  const html = useAnimation();
  const css = useAnimation();
  const js = useAnimation()

  useEffect(() => {
    if (inView) {
      html.start({
        width: "100%",
        transition: { type: "keyframes", duration: 3 },
      });
    }
    if (!inView) {
      html.start({ width: "1%" });
    }
  }, [inView, html]);

  return (
    <SectionWrapper id={"skills"} ref={ref} invertedColor>
      <div className={s.skillsContainer}>
        <Heading centered>
          My <span>skills</span>
        </Heading>

        <div className={s.skillsRow}>
          <div className={s.skillsColumn}>
            <h3 className={s.title}>Coding skills</h3>

            <div className={s.skillsBox}>
              <div className={s.skillsContent}>
                <div className={s.progress}>
                  <h3>
                    Html <span>100%</span>
                  </h3>
                  <div className={s.bar}>
                    <motion.span
                      className={s.htmlSkill}
                      animate={html}
                    ></motion.span>
                  </div>
                </div>
                <div className={s.progress}>
                  <h3>
                    CSS <span>100%</span>
                  </h3>
                  <div className={s.bar}>
                    <span id={"cssSkillAppear"}></span>
                  </div>
                </div>
                <div className={s.progress}>
                  <h3>
                    JavaScript / TypeScript <span>80%</span>
                  </h3>
                  <div className={s.bar}>
                    <span id={"jsSkillAppear"}></span>
                  </div>
                </div>
                <div className={s.progress}>
                  <h3>
                    React / Redux <span>70%</span>
                  </h3>
                  <div className={s.bar}>
                    <span id={"reactSkillAppear"}></span>
                  </div>
                </div>
                <div className={s.progress}>
                  <h3>
                    RestAPI <span>70%</span>
                  </h3>
                  <div className={s.bar}>
                    <span id={"restApiSkillAppear"}></span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </SectionWrapper>
  );
};

export default SkillsSection;
