import React from "react";
import Button from "../../shared/Button";
import SocialLink from "../../shared/SocialLinks";
import styled from "styled-components";
import photo from "../../assets/IMG_1775.jpeg"
import Heading from "../../shared/Heading";
import s from "./homeSection.module.css"

const HomeSection = () => {
    const HomeContent = styled.div`
      width: 60%;
      & h1 {
        font-size: 5rem;
        font-weight: 700;
        line-height: 1.3;
      }

      & p {
        font-size: 1.5rem;
        margin-top: 2rem;
        margin-bottom: 4rem;
      }
    `;

    const TextAnimate = styled.div`
      position: relative;
      width: 100%;

      & h3 {
        font-size: 3.2rem;
        font-weight: 700;
        color: transparent;
        -webkit-text-stroke: 0.7px var(--main-color);
      }
    `;

    const ButtonBox = styled.div`
      display: flex;
      justify-content: flex-start;
      gap: 3rem;
    `;

    const HomeSocials = styled.div`
      position: absolute;
      bottom: 4rem;
      width: 100%;
      display: flex;
      justify-content: flex-start;
      gap: 2rem;

      @media (max-width: 575.98px) {
        justify-content: center;
      }
    `;

    const HomePhoto = styled.div`
      width: 35rem;
      height: 35rem;
      background-image: url(${photo});
      background-size: cover;
      border: 0.5rem solid var(--main-color);
      border-radius: 50%;
      transition: .5s;
    `;

    return (
        // <SectionWrapper
        //     alignItems={"center"}
        //     justifyContent={"space-between"}
        //     flexDirection={"row"}
        //     sectionId={"home"}
        //     gap={"5rem"}
        //     reversedMedia
        // >
        <section className={s.home}>
            <div>
                <HomeContent>
                    <Heading>
                        Hi, I`m <span>Danila Nagovicin</span>
                    </Heading>
                    <TextAnimate>
                        <h3>Front-End Developer</h3>
                    </TextAnimate>
                    <p>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Dicta dolor dolorem doloremque earum eius
                        error iure minima minus odit officiis recusandae, reprehenderit totam voluptatem voluptatibus.
                    </p>
                    <ButtonBox>
                        <Button title={"Hire Me"} theme={"dark"}/>
                        <Button title={"Let`s talk"} theme={"light"}/>
                    </ButtonBox>
                </HomeContent>

                <HomeSocials>
                    <SocialLink socialMedia={"FaceBook"}/>
                    <SocialLink socialMedia={"LinkedIn"}/>
                    <SocialLink socialMedia={"Twitter"}/>
                </HomeSocials>

                <HomePhoto/>
            </div>
        </section>
        // </SectionWrapper>
    );
};

export default HomeSection;
