import styled from "styled-components";

type SectionProps = {
  invertedColor: boolean
}

export const SectionWrapper = styled.section<SectionProps>`
  position: relative;
  min-height: 100vh;
  width: 100vw;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${props => {
    if (props.invertedColor) {
      return "var(--second-bg-color)"
    }
  }};
`;
