import React, { memo, useRef } from "react";
import s from "./contactSection.module.css";
import Heading from "../../shared/Heading";
import Button from "../../shared/Button";
import { SectionWrapper } from "../../shared/SectionWrapper";
import { useFormik } from "formik";
import emailjs from "@emailjs/browser";

const ContactSection = memo(() => {
  const form = useRef(null)
  // const formik = useFormik({
  //   initialValues: {
  //     from_name: "",
  //     user_email: "",
  //     user_phone: "",
  //     some_additionals: "",
  //     message: "",
  //   },
  //   onSubmit: (values) => {

  //   },
  // });

  const handleClick = () => {
    emailjs.sendForm('service_5rwdk5g', 'template_79lqrda', JSON.stringify(formik.values), 'niKAnRJCetimFhpXR')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  }

  return (
    <SectionWrapper id={"contacts"} invertedColor>
      <div className={s.contactContainer}>
        <Heading>
          Contact <span>Me</span>
        </Heading>

        <form ref={form} className={s.contactForms}>
          <div className={s.inputBox}>
            <div className={s.inputField}>
              <input
                type="text"
                placeholder={"Name"}
                // {...formik.getFieldProps("from_name")}
              />
              <span className={s.focus}></span>
            </div>
            <div className={s.inputField}>
              <input
                type="text"
                placeholder={"Contact E-Mail"}
                // {...formik.getFieldProps("user_email")}
              />
              <span className={s.focus}></span>
            </div>
          </div>

          <div className={s.inputBox}>
            <div className={s.inputField}>
              <input
                type="text"
                placeholder={"Contact phone / telegram (optional)"}
                // {...formik.getFieldProps("user_phone")}
              />
              <span className={s.focus}></span>
            </div>
            <div className={s.inputField}>
              <input
                type="text"
                placeholder={"Some additional contacts (optional)"}
                // {...formik.getFieldProps("some_additionals")}
              />
              <span className={s.focus}></span>
            </div>
          </div>

          <div className={s.textAreaField}>
            <textarea
              cols={30}
              rows={10}
              placeholder={"Your message"}

              // {...formik.getFieldProps("message")}
            ></textarea>
            <span className={s.focus}></span>
          </div>
          <input type="submit" className={s.submitButton} onClick={handleClick}/>
        </form>
      </div>
    </SectionWrapper>
  );
});

export default ContactSection;
