import React from "react";
import s from "./homeSection.module.css";
import Button from "../../shared/button/Button";
import SocialLink from "../../shared/socialLinks/SocialLinks";
import styled from "styled-components";

const HomeSection = () => {
  const HomeWrapper = styled.section`
    padding: 10rem;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 9%;
    width: 100vw;
  `;

  const HomeContent = styled.div`
    max-width: 100%;

    & h1 {
      font-size: 5.6rem;
      font-weight: 700;
      line-height: 1.3;
    }
    & p {
      font-size: 1.6rem;
      margin: 2rem 0 4rem;
    }
  `;

  const TextAnimate = styled.div`
    position: relative;
    width: 100%;

    & h3 {
      font-size: 3.2rem;
      font-weight: 700;
      color: transparent;
      -webkit-text-stroke: 0.7px var(--main-color);ß
    }
  `;

  const ButtonBox = styled.div``;

  return (
    <HomeWrapper id="home">
      <HomeContent>
        <h1>
          Hi, I`m <span>Danila Nagovicin</span>
        </h1>
        <TextAnimate>
          <h3>Front-End Developer</h3>
        </TextAnimate>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam,
          laudantium.
        </p>
        <div className={s.btnBox}>
          <Button title={"Hire Me"} theme={"dark"} />
          <Button title={"Let`s talk"} theme={"light"} />
        </div>
      </HomeContent>

      <div className={s.homeSci}>
        <SocialLink socialMedia={"FaceBook"} />
        <SocialLink socialMedia={"LinkedIn"} />
        <SocialLink socialMedia={"Twitter"} />
      </div>

      <div className={s.homePhoto}></div>
    </HomeWrapper>
  );
};

export default HomeSection;
