import React from 'react';
import styled from "styled-components";
import {UilPalette} from "@iconscout/react-unicons";

const FixedButton = () => {
    const FixedBtnStyled = styled.div`
      position: fixed;
      bottom: 2rem;
      left: 4rem;
      padding: 1rem;
      background: var(--main-color);
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      cursor: pointer;
      transition: .5s;
      z-index: 100;

      &:hover {
        transform: scale(120%);
      }
    `

    return (
        <FixedBtnStyled onClick={() => {}}>
            <UilPalette size={"4rem"} />
        </FixedBtnStyled>
    );
};

export default FixedButton;
