import React, {FC, PropsWithChildren} from 'react';
import styled from "styled-components";

type WrapperProps = {
    alignItems: "center" | "flex-start"
    justifyContent: "space-between" | "center" | "flex-start" | "space-evenly"
    flexDirection: "row" | "column"
    backgroundColor?: string
    gap?: string
    sectionId: string
    padding?: string
    paddingT?: string
    paddingB?: string
    paddingL?: string
    paddingR?: string
    reversedMedia?: boolean
    height?: string
}

const SectionWrapper: FC<PropsWithChildren<WrapperProps>> = (props) => {

    const StyledSection = styled.section`
      display: flex;
      flex-wrap: wrap;
      align-items: ${props.alignItems};
      justify-content: ${props.justifyContent};
      flex-direction: ${props.flexDirection};
      padding: 0 9%;
      height: ${props.height};
      min-height: 50vh;
      width: 100%;
      background: ${props.backgroundColor};
      gap: ${props.gap};
      padding-top: ${props.paddingT};
      padding-bottom: ${props.paddingB};
      padding-left: ${props.paddingL};
      padding-right: ${props.paddingR};

      @media (max-width: 575.98px) {
        justify-content: center;
        align-items: center;
        flex-direction: ${props.reversedMedia ? "column-reverse" : "column"};
      }
    `

    return (
        <StyledSection id={props.sectionId} style={{padding: `${props.padding}`}}>
            {props.children}
        </StyledSection>
    );
};

export default SectionWrapper;
