import React, { useState } from "react";
import s from "./projectsSection.module.css";
import Heading from "../../shared/Heading";
import { SectionWrapper } from "../../shared/SectionWrapper";
import Button from "../../shared/Button";
import { UilArrow, UilArrowRight, UilReact } from "@iconscout/react-unicons";

const ProjectsSection = () => {
  const [filterNavItems, setFilterNavItems] = useState([
    { id: 1, title: "Native JS/TS", isActive: true },
    { id: 2, title: "React", isActive: false },
    { id: 3, title: "Vue", isActive: false },
    { id: 4, title: "Angular", isActive: false },
  ]);

  const navItemsForRender = () => {
    return filterNavItems.map((navE) => {
      return (
        <div
          className={`${s.filterItem} ${navE.isActive ? s.active : undefined}`}
          onClick={() => {
            const newState = [...filterNavItems];
            newState.forEach((e) => (e.isActive = false));
            const searchedIndex = newState.findIndex((e) => e.id === navE.id);
            newState[searchedIndex].isActive = true;
            setFilterNavItems(newState);
          }}
        >
          {navE.title}
        </div>
      );
    });
  };

  return (
    <SectionWrapper id="projects">
      <div className={s.projectsSectionContent}>
        <Heading centered>
          My <span>Projects</span>
        </Heading>
        <div className={s.filter}>{navItemsForRender()}</div>

        <div className={s.projects}>
          <div className={s.card}>
            <div className={s.backSideBox}>
              <img
                src="https://99designs-blog.imgix.net/blog/wp-content/uploads/2018/10/attachment_100040756-e1538485934255.jpeg?auto=format&q=60&fit=max&w=930"
                alt=""
              />
            </div>
            <div className={s.content}>
              <h2>TodoList</h2>
              <ul className={s.technologies}>
                <li>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 50 50"
                    width="30"
                    height="30"
                  >
                    <path d="M 5 4 A 1.0001 1.0001 0 0 0 4 5 L 4 45 A 1.0001 1.0001 0 0 0 5 46 L 45 46 A 1.0001 1.0001 0 0 0 46 45 L 46 5 A 1.0001 1.0001 0 0 0 45 4 L 5 4 z M 6 6 L 44 6 L 44 44 L 6 44 L 6 6 z M 15 23 L 15 26.445312 L 20 26.445312 L 20 42 L 24 42 L 24 26.445312 L 29 26.445312 L 29 23 L 15 23 z M 36.691406 23.009766 C 33.576782 22.997369 30.017578 23.941219 30.017578 28.324219 C 30.017578 34.054219 37.738281 34.055625 37.738281 36.640625 C 37.738281 36.885625 37.842187 38.666016 35.117188 38.666016 C 32.392187 38.666016 30.121094 36.953125 30.121094 36.953125 L 30.121094 41.111328 C 30.121094 41.111328 42.001953 44.954062 42.001953 36.289062 C 42.000953 30.664063 34.208984 30.945391 34.208984 28.150391 C 34.208984 27.067391 34.978375 26.054687 37.109375 26.054688 C 39.240375 26.054688 41.126953 27.3125 41.126953 27.3125 L 41.267578 23.607422 C 41.267578 23.607422 39.113892 23.019408 36.691406 23.009766 z" />
                  </svg>
                  <div>
                    TypeScript
                  </div>
                </li>
                <li>Redux ToolKit</li>
                <li>Jest Test</li>
                <li>StoryBook</li>
                <li>Axios</li>
              </ul>
              <div className={s.btnBox}>Click for more info</div>
            </div>
          </div>
        </div>
      </div>
    </SectionWrapper>
  );
};

export default ProjectsSection;
