import React from "react";
import styles from "./Hero.module.css";
import ThemeSwitcher from "../../../shared/themeSwitcher/themeSwitcher";

const Hero = () => {
  return (
    <section className={styles.heroWrapper} id={"hero"}>
      <ThemeSwitcher />
      {/* <div className={styles.decorationOval}/> */}
      <div className={styles.heroContainer}>
        <div className={styles.heroDescription}>
          <h2>Hello, I am</h2>
          <h1>Danila Nagovicin</h1>
          <div className={styles.typingText}>
            <h2>Front-End Developer</h2>
          </div>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquam,
            blanditiis, nihil? Aliquam distinctio ex, molestias obcaecati rerum
            similique tempore veniam!
          </p>
          <div className={styles.heroButton}>
            <div className={styles.buttonFront}>Download CV</div>
            <div className={styles.buttonBack}></div>
          </div>
        </div>
        <div className={styles.heroPhoto} />
      </div>
    </section>
  );
};

export default Hero;
