import {createGlobalStyle} from "styled-components";



export const blueColorPalette = {
    "bg-color": "#081b29",
    "second-bg-color": "#112e42",
    "text-color": "#ededed",
    "main-color": "#00abf0",
}

export const redColorPalette = {
    "bg-color": "#0E1116",
    "second-bg-color": "#586067",
    "text-color": "#F1F5F2",
    "main-color": "#FF331F"
}

export let globalColorPalette = blueColorPalette

export const GlobalStyle = createGlobalStyle`
      :root {
        --bg-color: ${globalColorPalette["bg-color"]};
        --second-bg-color: ${globalColorPalette["second-bg-color"]};
        --text-color: ${globalColorPalette["text-color"]};
        --main-color: ${globalColorPalette["main-color"]};
      }
    `
