import React, {useState} from "react";
import s from "./header.module.css";
import {UilBars, UilBox} from "@iconscout/react-unicons";
import NavLink from "../../shared/navLink/NavLink";

const Header = () => {
    const [isActiveHome, setActiveHome] = useState(true)
    const [isActiveAbout, setActiveAbout] = useState(false)
    const [isActiveEducation, setActiveEducation] = useState(false)
    const [isActiveSkills, setActiveSkills] = useState(false)
    const [isActiveContacts, setActiveContacts] = useState(false)

    const activeHomeSetter = () => {
        setActiveHome(true)
        setActiveAbout(false)
        setActiveEducation(false)
        setActiveSkills(false)
        setActiveContacts(false)
    }
    const activeAboutSetter = () => {
        setActiveHome(false)
        setActiveAbout(true)
        setActiveEducation(false)
        setActiveSkills(false)
        setActiveContacts(false)
    }
    const activeEducationSetter = () => {
        setActiveHome(false)
        setActiveAbout(false)
        setActiveEducation(true)
        setActiveSkills(false)
        setActiveContacts(false)
    }
    const activeSkillsSetter = () => {
        setActiveHome(false)
        setActiveAbout(false)
        setActiveEducation(false)
        setActiveSkills(true)
        setActiveContacts(false)
    }
    const activeContactsSetter = () => {
        setActiveHome(false)
        setActiveAbout(false)
        setActiveEducation(false)
        setActiveSkills(false)
        setActiveContacts(true)
    }

    return (
        <header className={s.header}>
            <a href="#" className={s.logo}>
                Danila-beep
            </a>

            <UilBars display={"none"} cursor={"pointer"} color={"#ededed"} size={40}/>

            <nav className={s.navbar}>
                <NavLink title={"Home"} href={"#home"} isActive={isActiveHome} activeSetter={activeHomeSetter}/>
                <NavLink title={"About"} href={"#about"} activeSetter={activeAboutSetter}/>
                <NavLink title={"Educations"} href={"#education"} activeSetter={activeEducationSetter}/>
                <NavLink title={"Skills"} href={"#"} activeSetter={activeSkillsSetter}/>
                <NavLink title={"Contacts"} href={"#"} activeSetter={activeContactsSetter}/>
            </nav>

        </header>
    );
};

export default Header;
