import React from 'react'
import s from "./aboutSection.module.css"
import Button from "../../shared/button/Button";
import SectionWrapper from "../../shared/SectionWrapper";

const AboutSection = () => {
    return (
        <SectionWrapper
            alignItems={"center"}
            justifyContent={"center"}
            flexDirection={"column"}
            backgroundColor={"var(--second-bg-color)"}
            gap={"10rem"}
        >
            <h2 className={s.heading}>About <span>Me</span></h2>

            <div className={s.aboutImgWrapper}>
                <div className={s.aboutPhoto}/>
                <span className={s.circleSpin}></span>
            </div>

            <div className={s.aboutContent}>
                <h3>Front-End Developer</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Blanditiis delectus, eaque, est eveniet
                    facere id impedit, laudantium magnam mollitia porro quis tempora velit veniam. Accusamus, aperiam
                    aspernatur dicta dolore, expedita fugit, hic illum ipsa libero possimus quasi recusandae saepe
                    sed.</p>

                <div className={s.btnBox}>
                    <Button title={"Read More"} theme={"dark"}/>
                </div>
            </div>
        </SectionWrapper>
    )
}

export default AboutSection
