import React, {useEffect} from 'react';
import s from "./skillsSection.module.css"
import Heading from "../../shared/Heading";
import {useInView} from "react-intersection-observer";

const SkillsSection = () => {
    const [ref, inView] = useInView()

    useEffect(() => {
        if (inView) {
            let progressBar = document.getElementById("appearance")
            progressBar?.classList.toggle(s.appear)
        }
    }, [inView])


    return (
        <section className={s.skillsWrapper} id={"skills"} ref={ref}>
            <div className={s.skillsContainer}>
                <Heading centered>My <span>skills</span></Heading>

                <div className={s.skillsRow}>
                    <div className={s.skillsColumn}>
                        <h3 className={s.title}>Coding skills</h3>

                        <div className={s.skillsBox}>
                            <div className={s.skillsContent}>
                                <div className={s.progress}>
                                    <h3>Html <span>90%</span></h3>
                                    <div className={s.bar}><span id={"appearance"}></span></div>
                                </div>
                                <div className={s.progress}>
                                    <h3>Html <span>90%</span></h3>
                                    <div className={s.bar}><span></span></div>
                                </div>
                                <div className={s.progress}>
                                    <h3>Html <span>90%</span></h3>
                                    <div className={s.bar}><span></span></div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className={s.skillsColumn}>
                        <h3 className={s.title}>Coding skills</h3>

                        <div className={s.skillsBox}>
                            <div className={s.skillsContent}>
                                <div className={s.progress}>
                                    <h3>Html <span>90%</span></h3>
                                    <div className={s.bar}><span></span></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default SkillsSection;
