import React from "react";
import Button from "../../shared/button/Button";
import SocialLink from "../../shared/socialLinks/SocialLinks";
import styled from "styled-components";
import photo from "../../assets/IMG_1775.jpeg"
import SectionWrapper from "../../shared/SectionWrapper";

const HomeSection = () => {
    const HomeContent = styled.div`
      max-width: 70%;

      & h1 {
        font-size: 5.3rem;
        font-weight: 700;
        line-height: 1.3;
      }

      & p {
        font-size: 1.6rem;
        margin: 2rem 0 4rem;
      }
    `;

    const TextAnimate = styled.div`
      position: relative;
      width: 100%;

      & h3 {
        font-size: 3.2rem;
        font-weight: 700;
        color: transparent;
        -webkit-text-stroke: 0.7px var(--main-color);
      }
    `;

    const ButtonBox = styled.div`
      position: relative;
      display: flex;
      justify-content: space-between;
      width: 34.5rem;
      height: 5rem;
    `;

    const HomeSocials = styled.div`
      position: absolute;
      bottom: 4rem;
      width: 100%;
      display: flex;
      justify-content: flex-start;
    `;

    const HomePhoto = styled.div`
      width: 300px;
      height: 300px;
      background-image: url(${photo});
      background-size: cover;
      background-position: center;
      border: 0.5rem solid var(--main-color);
      border-radius: 50%;
    `;

    return (
        <SectionWrapper
            alignItems={"center"}
            justifyContent={"space-between"}
            flexDirection={"row"}
        >
            <HomeContent>
                <h1>
                    Hi, I`m <span>Danila Nagovicin</span>
                </h1>
                <TextAnimate>
                    <h3>Front-End Developer</h3>
                </TextAnimate>
                <p>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit. Dicta dolor dolorem doloremque earum eius
                    error iure minima minus odit officiis recusandae, reprehenderit totam voluptatem voluptatibus.
                </p>
                <ButtonBox>
                    <Button title={"Hire Me"} theme={"dark"}/>
                    <Button title={"Let`s talk"} theme={"light"}/>
                </ButtonBox>
            </HomeContent>

            <HomeSocials>
                <SocialLink socialMedia={"FaceBook"}/>
                <SocialLink socialMedia={"LinkedIn"}/>
                <SocialLink socialMedia={"Twitter"}/>
            </HomeSocials>

            <HomePhoto/>
        </SectionWrapper>
    );
};

export default HomeSection;
