                    aboutSecTitle: "Обо",
                    aboutSecTitleColored: "Мне",
                    aboutButtonTitle: "Узнать больше",
                    educationSecTitle1: "",
                    educationColored: "Образование",
                    educationSecTitle2: "и",
                    experienceColored: "Опыт Работы"

                }
            },
            en: {
                translation: {
                    homeTitleNav: "Home",
                    aboutTitleNav: "About",
                    educationTitleNav: "Education",
                    skillsTitleNav: "Skills",
                    contactsTitleNav: "Contacts",
