import React from "react";
import Button from "../../shared/Button";
import Heading from "../../shared/Heading";
import s from "./homeSection.module.css"
import SocialLink from "../../shared/SocialLinks";
import {useTranslation} from "react-i18next";

const HomeSection = () => {
    const {t} = useTranslation()

    return (
        <section className={s.homeWrapper} id={"home"}>
            <div className={s.homeContainer}>

                <div className={s.homeContent}>
                    <Heading>{t("helloMessage")} <span>{t("myName")}</span></Heading>

                    <div className={s.textAnimate}>
                        <h3>Front-End {t("developerMessage")}</h3>
                    </div>
                    <p>{t("homePageContent")}</p>
                    <div className={s.buttonBox}>
                        <Button title={"Hire Me"} theme={"dark"}/>
                        <Button title={"Let`s talk"} theme={"light"}/>
                    </div>
                </div>
                <div className={s.homePhoto}></div>
            </div>
        </section>
    );
};

export default HomeSection;
