import React, {useState} from "react";
import s from "./header.module.css";
import {UilBars} from "@iconscout/react-unicons";
import NavLink from "../../shared/NavLink";

const Header = () => {
    const [isActiveHome, setActiveHome] = useState(true)
    const [isActiveAbout, setActiveAbout] = useState(false)
    const [isActiveEducation, setActiveEducation] = useState(false)
    const [isActiveSkills, setActiveSkills] = useState(false)
    const [isActiveContacts, setActiveContacts] = useState(false)

    const [isBurgerMenuShowed, setBurgerMenuShowed] = useState(true)

    const activeHomeSetter = () => {
        setActiveHome(true)
        setActiveAbout(false)
        setActiveEducation(false)
        setActiveSkills(false)
        setActiveContacts(false)
    }
    const activeAboutSetter = () => {
        setActiveHome(false)
        setActiveAbout(true)
        setActiveEducation(false)
        setActiveSkills(false)
        setActiveContacts(false)
    }
    const activeEducationSetter = () => {
        setActiveHome(false)
        setActiveAbout(false)
        setActiveEducation(true)
        setActiveSkills(false)
        setActiveContacts(false)
    }
    const activeSkillsSetter = () => {
        setActiveHome(false)
        setActiveAbout(false)
        setActiveEducation(false)
        setActiveSkills(true)
        setActiveContacts(false)
    }
    const activeContactsSetter = () => {
        setActiveHome(false)
        setActiveAbout(false)
        setActiveEducation(false)
        setActiveSkills(false)
        setActiveContacts(true)
    }

    window.onscroll = () => {
        let header = document.querySelector("header")

        header?.classList.toggle(s.sticky, window.scrollY > 100)
    }

    return (
        <>
            <header className={s.header}>
                <a href="#" className={s.logo}>
                    Danila-beep
                </a>

                <div className={s.burger} onClick={() => {
                }}>
                    <UilBars size={40}/>
                </div>

                <nav className={s.navbar}>
                    <NavLink title={"Home"} href={"#home"} isActive={isActiveHome} activeSetter={activeHomeSetter}/>
                    <NavLink title={"About"} href={"#about"} isActive={isActiveAbout} activeSetter={activeAboutSetter}/>
                    <NavLink title={"Educations"} href={"#education"} isActive={isActiveEducation}
                             activeSetter={activeEducationSetter}/>
                    <NavLink title={"Skills"} href={"#skills"} isActive={isActiveSkills}
                             activeSetter={activeSkillsSetter}/>
                    <NavLink title={"Contacts"} href={"#contacts"} isActive={isActiveContacts}
                             activeSetter={activeContactsSetter}/>
                </nav>

            </header>

            {isBurgerMenuShowed
                ? <div style={{marginTop: "2rem"}}>123</div>
                : undefined
            }
        </>
    );
};

export default Header;
