import React, { FC } from "react";
import styled from "styled-components";

type NavLinkProps = {
    title: string
    href: string
    isActive?: boolean
}

const NavLink: FC<NavLinkProps> = (props) => {
  const NavStyled = styled.a`
    font-size: 1.7rem;
    color: var(--text-color);
    font-weight: 500;
    margin-left: 3.5rem;
    transition: 0.3s;

    &:hover {
      color: var(--main-color);
    }
  `;

  const activeStyle = () => {
      if (props.isActive) {
         return {
             color: "var(--main-color)"
      }
      }
  }

  return <NavStyled href={props.href} style={activeStyle}>{props.title}</NavStyled>;
};

export default NavLink;
