import React, { FC } from "react";
import styled from "styled-components";

type ThemesTypes = "dark" | "light"

type ButtonProps = {
    title: string,
    theme: ThemesTypes
}

const Button: FC<ButtonProps> = (props) => {
  const Btn = styled.a`
    position: relative;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    width: 15rem;
    height: 100%;
    background: {if};
    border: 0.2rem solid var(--main-color);
    border-radius: 0.8rem;
    font-size: 1.8rem;
    font-weight: 600;
    letter-spacing: 0.1rem;
    color: var(--bg-color);
    z-index: 1;
    overflow: hidden;
    transition: 0.5s;

    &:hover {
      color: var(--main-color);
    }
    &::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 0;
      height: 100%;
      background: var(--bg-color);
      z-index: -1;
      transition: 0.5s;
    }
    &:hover::before {
      width: 100%;
    }
  `;

  return (
    <Btn>
        {props.title}
    </Btn>
  );
};

export default Button;
