import React, { useState } from "react";
import "./App.css";
import Header from "./Components/Header/Header";
import HomeSection from "./Components/HomeSection/HomeSection";
import AboutSection from "./Components/AboutSection/AboutSection";
import EducationSection from "./Components/EducationSection/EducationSection";
import SkillsSection from "./Components/SkillsSection/SkillsSection";
import ContactSection from "./Components/ContactSection/ContactSection";
import Footer from "./Components/Footer/Footer";
import BurgerMenu from "./Components/BurgerMenu/BurgerMenu";
import GlobalStyles from "./utils/GlobalStyles";
import { blueColorPalette } from "./shared/ColorThemes";
import FixedButton from "./shared/FixedButton";
import ProjectsSection from "./Components/ProjectsSection/ProjectsSection";

type ThemeType = {
  "bg-color": string;
  "second-bg-color": string;
  "text-color": string;
  "main-color": string;
};

export type NavLinksNamesType = {
  Home: boolean;
  About: boolean;
  Education: boolean;
  Skills: boolean;
  Project: boolean;
  Contacts: boolean;
};

function App() {
  const [isActive, setIsActive] = useState<NavLinksNamesType>({
    Home: true,
    About: false,
    Education: false,
    Skills: false,
    Project: false,
    Contacts: false,
  });
  // Active State & Setters
  const [isActiveHome, setActiveHome] = useState(true);
  const [isActiveAbout, setActiveAbout] = useState(false);
  const [isActiveEducation, setActiveEducation] = useState(false);
  const [isActiveSkills, setActiveSkills] = useState(false);
  const [isActiveProjects, setActiveProjects] = useState(false);
  const [isActiveContacts, setActiveContacts] = useState(false);
  const activeHomeSetter = () => {
    setActiveHome(true);
    setActiveAbout(false);
    setActiveEducation(false);
    setActiveSkills(false);
    setActiveProjects(false);
    setActiveContacts(false);
  };
  const activeAboutSetter = () => {
    setActiveHome(false);
    setActiveAbout(true);
    setActiveEducation(false);
    setActiveSkills(false);
    setActiveProjects(false);
    setActiveContacts(false);
  };
  const activeEducationSetter = () => {
    setActiveHome(false);
    setActiveAbout(false);
    setActiveEducation(true);
    setActiveSkills(false);
    setActiveProjects(false);
    setActiveContacts(false);
  };
  const activeSkillsSetter = () => {
    setActiveHome(false);
    setActiveAbout(false);
    setActiveEducation(false);
    setActiveSkills(true);
    setActiveProjects(false);
    setActiveContacts(false);
  };
  const activeProjectsSetter = () => {
    setActiveHome(false);
    setActiveAbout(false);
    setActiveEducation(false);
    setActiveSkills(false);
    setActiveProjects(true);
    setActiveContacts(false);
  };
  const activeContactsSetter = () => {
    setActiveHome(false);
    setActiveAbout(false);
    setActiveEducation(false);
    setActiveSkills(false);
    setActiveProjects(false);
    setActiveContacts(true);
  };

  const setActiveBtnNav = (e: any) => {
    const name = e.currentTarget.name as NavLinksNamesType;

    setIsActive((value) => {
      const resArr = { ...value };
      for (let v in resArr) {
        //@ts-ignore
        resArr[v] = false;
      }

      //@ts-ignore
      resArr[name] = true;

      return resArr;
    });
  };

  console.log(isActive);

  //TODO: Узнать как можно сократить | как сделать переключение активных стилей при скролле

  // Burger menu
  const [isBurgerMenuShowed, setBurgerMenuShowed] = useState(false);
  const showBurgerMenu = () => {
    setBurgerMenuShowed(!isBurgerMenuShowed);
  };

  // Theme changer
  const [globalColorPalette, setGlobalColorPalette] =
    useState(blueColorPalette);
  const themeSwitcher = (theme: ThemeType) => {
    setGlobalColorPalette(theme);
  };
  const [isColorMenuShowed, setColorMenuShowed] = useState(false);
  const themeMenuSetter = (value: boolean) => {
    setColorMenuShowed(value);
  };

  // Lang changer

  return (
    <div className={"App"}>
      <GlobalStyles globalColorPalette={globalColorPalette} />
      <Header
        // isActiveHome={isActiveHome}
        // isActiveAbout={isActiveAbout}
        // isActiveEducation={isActiveEducation}
        // isActiveSkills={isActiveSkills}
        // isActiveProjects={isActiveProjects}
        // isActiveContacts={isActiveContacts}
        // activeHomeSetter={activeHomeSetter}
        // activeAboutSetter={activeAboutSetter}
        // activeEducationSetter={activeEducationSetter}
        // activeSkillsSetter={activeSkillsSetter}
        // activeProjectsSetter={activeProjectsSetter}
        // activeContactsSetter={activeContactsSetter}

        isBurgerMenuShowed={isBurgerMenuShowed}
        showBurgerMenu={showBurgerMenu}
        isActive={isActive}
        setActiveBtnNav={setActiveBtnNav}
      />
      {isBurgerMenuShowed ? (
        <BurgerMenu
          isActiveHome={isActiveHome}
          isActiveAbout={isActiveAbout}
          isActiveEducation={isActiveEducation}
          isActiveSkills={isActiveSkills}
          isActiveProjects={isActiveProjects}
          isActiveContacts={isActiveContacts}
          activeHomeSetter={activeHomeSetter}
          activeAboutSetter={activeAboutSetter}
          activeEducationSetter={activeEducationSetter}
          activeSkillsSetter={activeSkillsSetter}
          activeProjectsSetter={activeProjectsSetter}
          activeContactsSetter={activeContactsSetter}
          isBurgerMenuShowed={isBurgerMenuShowed}
          showBurgerMenu={showBurgerMenu}
        />
      ) : undefined}

      <HomeSection />
      <AboutSection />
      <EducationSection />
      <SkillsSection />
      <ProjectsSection />
      <ContactSection />
      <Footer />
      <FixedButton
        themeSwitcher={themeSwitcher}
        themeMenuSetter={themeMenuSetter}
        isColorMenuShowed={isColorMenuShowed}
      />
    </div>
  );
}

export default App;
