import React, {useState} from 'react';
import {createGlobalStyle} from "styled-components";

const GlobalStyles = () => {

    const blueColorPalette = {
        "bg-color": "#081b29",
        "second-bg-color": "#112e42",
        "text-color": "#ededed",
        "main-color": "#00abf0",
    }

    const redColorPalette = {
        "bg-color": "#0E1116",
        "second-bg-color": "#25252c",
        "text-color": "#F1F5F2",
        "main-color": "#FF331F"
    }

    const brownColorPalette = {
        "bg-color": "#261C15",
        "second-bg-color": "#564b3d",
        "text-color": "#FEFAE0",
        "main-color": "#D5896F"
    }

    const pinkColorPalette = {
        "bg-color": "#1D3354",
        "second-bg-color": "#1F516B",
        "text-color": "#E6EBE0",
        "main-color": "#DABFFF"
    }



    const [globalColorPalette , setGobalColorPalette] = useState(blueColorPalette)

    const GlobalStyle = createGlobalStyle`
      :root {
        --bg-color: ${globalColorPalette["bg-color"]};
        --second-bg-color: ${globalColorPalette["second-bg-color"]};
        --text-color: ${globalColorPalette["text-color"]};
        --main-color: ${globalColorPalette["main-color"]};
      }
    `

    return (
        <GlobalStyle />
    );
};

export default GlobalStyles;
